using BeatSaberMarkupLanguage;
using BeatSaberMarkupLanguage.Attributes;
using GuildSaber.API;
using GuildSaber.Logger;
using GuildSaber.Utils;
using HMUI;
using IPA.Utilities;
using Newtonsoft.Json;
using PlaylistManager.Utilities;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Net;
using System.Net.Http;
using System.Threading.Tasks;
using TMPro;
using UnityEngine.UI;
using Color = UnityEngine.Color;


#nullable enable
namespace GuildSaber.UI.GuildSaber
{
    public class CategoryUI
    {
        private const string EMPTY_IMAGE = "\"image\":\"\"";

        [UIComponent("CategoryNameText")] private readonly TextMeshProUGUI m_CategoryNameText = null;
        [UIComponent("ClearCategoryButton")] private readonly Button m_ClearCategoryButton = null;
        [UIComponent("DownloadBut")] private readonly Button m_DownloadButton = null;
        [UIComponent("ElemsHorizontal")] public readonly HorizontalLayoutGroup m_HorizontalElems = null;

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        public int CurrentPlaylistIndex;
        public bool DownloadOnlyUnPassed;
        public string m_CategoryDirectory = "Undefined";
        internal List<ApiRankingLevel> m_CategoryLevels = new List<ApiRankingLevel>();
        public int m_GuildId = 1;
        public int PlaylistsCountInCategory;

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        public CategoryUI(ApiCategory p_Category, int p_GuildId, bool p_DownloadOnlyUnPassed)
        {
            Category = p_Category;
            m_GuildId = p_GuildId;
            DownloadOnlyUnPassed = p_DownloadOnlyUnPassed;
        }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        public ApiCategory Category { get; set; }

        private async void Init()
        {
            using (HttpClient l_Client = new HttpClient())
            {
                try
                {
                    ApiCategory category = Category;
                    string l_Str;
                    if (category.ID != 0)
                    {
                        category = Category;
                        l_Str = string.Format("&category-id={0}", category.ID);
                    }
                    else
                        l_Str = string.Empty;
                    string l_CategoryQueryString = l_Str;
                    string l_SerializedObject = await l_Client.GetStringAsync(string.Format("https://api.guildsaber.com/levels/data/all?guild-id={0}{1}", (object)m_GuildId, (object)l_CategoryQueryString));
                    List<ApiRankingLevel> l_TempValidLevels = JsonConvert.DeserializeObject<List<ApiRankingLevel>>(l_SerializedObject);
                    foreach (ApiRankingLevel apiRankingLevel in l_TempValidLevels)
                    {
                        ApiRankingLevel l_Index = apiRankingLevel;
                        m_CategoryLevels.Add(l_Index);
                        l_Index = new ApiRankingLevel();
                    }
                }
                catch (HttpRequestException ex)
                {
                    m_CategoryLevels = new List<ApiRankingLevel>();
                    GSLogger.Instance.Error(ex, "PlaylistDownloaderViewController", nameof(Init));
                }
                PlaylistsCountInCategory = m_CategoryLevels.Count;
                m_CategoryDirectory = "./Playlists/GuildSaber/" + GuildApi.GetGuildFromId(m_GuildId).Name + "/" + Category.Name;
                if (PlaylistsCountInCategory == 0)
                {
                    (m_DownloadButton).interactable = false;
                    m_DownloadButton.SetButtonText("Error");
                }
                CheckCategoryForButton();
            }
        }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        private async void CheckCategoryForButton()
        {
            await WaitUtils.Wait(() => m_ClearCategoryButton != null, 1);
            m_ClearCategoryButton.gameObject.SetActive(Directory.Exists(m_CategoryDirectory));
        }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        public void UnbindEvent() { /*GuildSaberModule.m_ModFlowCoordinator._modViewController.e_OnUnPassedOnlyValueChanged -= new PlaylistViewController.OnOnlyPassedMapsChange(OnUnPassedOnlyChanged)*/return; }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        [UIAction("#post-parse")]
        private void PostParse()
        {
            Init();
            ImageView l_ImageView = (m_HorizontalElems).GetComponent<ImageView>();
            ReflectionUtil.SetField(l_ImageView, "_skew", 0.0f);
            (l_ImageView).SetImage("#RoundRect10BorderFade");
            (l_ImageView).color = ColorExtensions.ColorWithAlpha(Color.white, 0.5f);
            l_ImageView.color0 = ColorExtensions.ColorWithAlpha(Color.white, 0.4f);
            l_ImageView.color1 = ColorExtensions.ColorWithAlpha(Color.white, 0.4f);
            (l_ImageView).overrideSprite = null;
            //GuildSaberModule.m_ModFlowCoordinator._modViewController.e_OnUnPassedOnlyValueChanged += OnUnPassedOnlyChanged;
            (m_CategoryNameText).text = Category.ID == 0 ? "Default" : Category.Name;
            new ButtonBinder().AddBinding(m_DownloadButton, new Action(PreDownloadPlaylist));
        }

        [UIAction("ClearCategoryFolder")]
        private void ClearCategory()
        {
            if (!Directory.Exists(m_CategoryDirectory))
                return;
            foreach (string file in Directory.GetFiles(m_CategoryDirectory))
            {
                if (file.EndsWith(".json") || file.EndsWith(".bplist"))
                    File.Delete(file);
            }
            if (!Directory.GetDirectories(m_CategoryDirectory).Any())
                Directory.Delete(m_CategoryDirectory);
            CheckCategoryForButton();
        }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        private void OnUnPassedOnlyChanged(bool p_UnPassedOnly) => DownloadOnlyUnPassed = p_UnPassedOnly;

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        private void PreDownloadPlaylist()
        {
            CheckAndDeleteIfPlaylistOrFolderExists(PlaylistsVerificationType.FolderOnly);
            DownloadPlaylist();
        }

        private async void DownloadPlaylist()
        {

            if (Category.Name == "Undefined" || m_CategoryLevels.Count == 0)
                return;

            if (CurrentPlaylistIndex == PlaylistsCountInCategory)
            {
                m_DownloadButton.SetButtonText("Finished");
                (m_DownloadButton).interactable = false;
                CheckCategoryForButton();
                PlaylistLibUtils.playlistManager.RefreshPlaylists(true);
                return;
            }

            try
            {
                WebClient l_Client = new WebClient();

                m_DownloadButton.SetButtonText($"Downloading {(float)(CurrentPlaylistIndex / (double)PlaylistsCountInCategory * 100.0):00}%");
                m_DownloadButton.interactable = false;

                ///////////////////////////////////////////////////////////////////////////////

                string l_CategoryQueryString = Category.ID != 0 ? $"category-id={Category.ID}" : string.Empty;
                string l_PlayerIDQuery = DownloadOnlyUnPassed ? $"?player-id={GuildSaberModule.GSPlayerId}&" : "?";
                ApiRankingLevel l_CategoryLevel = m_CategoryLevels[CurrentPlaylistIndex];
                string l_QueryString = $"https://api.guildsaber.com/playlists/data/by-id/{l_CategoryLevel.ID}{l_PlayerIDQuery}{l_CategoryQueryString}";

                ///////////////////////////////////////////////////////////////////////////////

                CheckAndDeleteIfPlaylistOrFolderExists(PlaylistsVerificationType.PlaylistsOnly);
                string l_Data = await l_Client.DownloadStringTaskAsync(new Uri(l_QueryString));
                if (m_CategoryLevels.Count > 15 && l_Data.Contains("\"image\":\"\""))
                    l_Data = l_Data.Replace(EMPTY_IMAGE, "\"image\":\"base64,\"");

                ///////////////////////////////////////////////////////////////////////////////

                l_CategoryLevel = m_CategoryLevels[CurrentPlaylistIndex];
                string l_GuildName = GuildApi.GetGuildFromId(PlaylistViewController.PlaylistsDownloadViewControllerInstance.m_GuildId).Name;
                string l_CategoryName = Category.Name;
                File.WriteAllText($"{m_CategoryDirectory}/{l_CategoryLevel.LevelNumber:000.00}_{l_GuildName.Replace(' ', '_')}{(l_CategoryName == string.Empty ? string.Empty : $"_{l_CategoryName}")}.bplist", l_Data);

                ///////////////////////////////////////////////////////////////////////////////

                m_DownloadButton.SetButtonText($"{(CurrentPlaylistIndex * 100 / m_CategoryLevels.Count):00.0}");
                CurrentPlaylistIndex++;
                DownloadPlaylist();

                ///////////////////////////////////////////////////////////////////////////////
            }
            catch (Exception l_E)
            {
                GSLogger.Instance.Error(l_E, nameof(CategoryUI), nameof(DownloadPlaylist));
                (m_DownloadButton).interactable = false;
                m_DownloadButton.SetButtonText("Error");
            }

        }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        private void CheckAndDeleteIfPlaylistOrFolderExists(PlaylistsVerificationType p_VerificationType)
        {
            while (true)
            {
                switch (p_VerificationType)
                {
                    case PlaylistsVerificationType.FolderOnly:
                        if (Directory.Exists(m_CategoryDirectory))
                            return;
                        Directory.CreateDirectory(m_CategoryDirectory);
                        break;
                    case PlaylistsVerificationType.PlaylistsOnly:
                        string path = CategoryUI.BuildLevelPath(m_CategoryDirectory, m_CategoryLevels[CurrentPlaylistIndex].LevelNumber, m_GuildId, Category.Name);
                        if (!File.Exists(path))
                            return;
                        File.Delete(path);
                        break;
                    case PlaylistsVerificationType.All:
                        CheckAndDeleteIfPlaylistOrFolderExists(PlaylistsVerificationType.FolderOnly);
                        p_VerificationType = PlaylistsVerificationType.PlaylistsOnly;
                        continue;
                    default: return;
                }
            }
        }

        ///////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////

        private static string BuildLevelPath(
          string p_CategoryDirectory,
          float p_LevelNumber,
          int p_GuildId,
          string p_CategoryName)
        {
            return $"{p_CategoryDirectory}/{p_LevelNumber:000.00}_{p_GuildId}{(p_CategoryName == string.Empty ? string.Empty : $"_{p_CategoryName}")}.bplist";
        }

        enum PlaylistsVerificationType
        {
            FolderOnly, PlaylistsOnly, All
        }
    }
}
